import React, { useState } from 'react';
import ticketsData from './tickets.json'; // Импортируем данные о билетах
import CurrencyConverter from './CurrencyConverter'; // Импортируем компонент для конвертации валют

const TicketList = () => {
  const [selectedStops, setSelectedStops] = useState([]); // Состояние для фильтрации по пересадкам
  const [selectedCurrency, setSelectedCurrency] = useState('USD'); // Состояние для валюты

  // Сортировка билетов по цене
  const sortedTickets = [...ticketsData.tickets].sort((a, b) => a.price - b.price);

  // Фильтрация билетов по выбранным пересадкам
  const filteredTickets = sortedTickets.filter(ticket => {
    const stops = Number(ticket.stops); // Преобразуем stops в число, если оно строка

    if (selectedStops.length === 0 || selectedStops.includes("all")) return true; // Если не выбраны фильтры или выбрано "Все", показываем все билеты
    return selectedStops.includes(stops); // Показываем билеты, если количество пересадок в списке выбранных
  });

  // Обработка выбора фильтра
  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    if (value === "all") {
      // Если выбрано "Все", сбрасываем фильтр
      setSelectedStops([value]);
    } else {
      if (e.target.checked) {
        setSelectedStops(prev => [...prev, value]); // Добавляем выбранный фильтр
      } else {
        setSelectedStops(prev => prev.filter(stops => stops !== value)); // Убираем снятый фильтр
      }
    }
  };

  // Обработка выбора валюты
  const handleCurrencyChange = (currency) => {
    setSelectedCurrency(currency); // Обновляем валюту
  };

  return (
    <div className="container">
      <div className="functions">
        <h1>Функции</h1>

        {/* Фильтрация по пересадкам через чекбоксы */}
        <div>
          <h3>Фильтр по пересадкам:</h3>
          <label>
            <input
              type="checkbox"
              value="all"
              onChange={handleCheckboxChange}
              checked={selectedStops.includes("all")}
            />
            Все
          </label>
          <br />
          <label>
            <input
              type="checkbox"
              value="0"
              onChange={handleCheckboxChange}
              checked={selectedStops.includes("0")}
            />
            Без пересадок
          </label>
          <br />
          <label>
            <input
              type="checkbox"
              value="1"
              onChange={handleCheckboxChange}
              checked={selectedStops.includes("1")}
            />
            1 пересадка
          </label>
          <br />
          <label>
            <input
              type="checkbox"
              value="2"
              onChange={handleCheckboxChange}
              checked={selectedStops.includes("2")}
            />
            2 пересадки
          </label>
          <br />
          <label>
            <input
              type="checkbox"
              value="3"
              onChange={handleCheckboxChange}
              checked={selectedStops.includes("3")}
            />
            3 пересадки
          </label>
        </div>

        {/* Кнопки для выбора валюты */}
        <div>
          <button onClick={() => handleCurrencyChange('USD')} className={selectedCurrency === 'USD' ? 'active' : ''}>USD</button>
          <button onClick={() => handleCurrencyChange('EUR')} className={selectedCurrency === 'EUR' ? 'active' : ''}>EUR</button>
          <button onClick={() => handleCurrencyChange('RUB')} className={selectedCurrency === 'RUB' ? 'active' : ''}>RUB</button>
        </div>
      </div>

      <div className="tickets">
        <h1>Билеты</h1>

        {/* Проверка, есть ли билеты для отображения */}
        {filteredTickets.length === 0 && (
          <p>Нет билетов для отображения по выбранному фильтру.</p>
        )}

        {/* Список билетов */}
        <ul>
          {filteredTickets.map(ticket => (
            <li 
              key={`${ticket.departure_date}-${ticket.departure_time}-${ticket.arrival_date}-${ticket.arrival_time}-${ticket.origin}-${ticket.destination}`}
            >
              <p>{ticket.origin_name} → {ticket.destination_name}</p>
              <p>Дата: {ticket.departure_date}, Время вылета: {ticket.departure_time}, Время прибытия: {ticket.arrival_time}</p>
              <p>Пересадки: {ticket.stops}</p>
              <CurrencyConverter priceInRub={ticket.price} selectedCurrency={selectedCurrency} />
              <p>Авиакомпания: {ticket.carrier}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TicketList;
