import React, { useState } from 'react';
import ticketsData from './tickets.json'; // Импортируем данные о билетах

const TicketList = () => {
  const [tickets, setTickets] = useState(ticketsData.tickets);
  const [filterStops, setFilterStops] = useState(0);

  // Сортировка билетов по цене
  const sortedTickets = [...tickets].sort((a, b) => a.price - b.price);

  // Фильтрация билетов по количеству пересадок
  const filteredTickets = sortedTickets.filter(ticket => ticket.stops >= filterStops);

  return (
    <div>
      <h1>Билеты</h1>

      {/* Фильтрация по пересадкам */}
      <div>
        <label htmlFor="stops">Фильтр по пересадкам:</label>
        <select id="stops" value={filterStops} onChange={e => setFilterStops(Number(e.target.value))}>
          <option value={0}>Без пересадок</option>
          <option value={1}>1 пересадка</option>
          <option value={2}>2 пересадки</option>
          <option value={3}>3 пересадки</option>
        </select>
      </div>

      {/* Список билетов */}
      <ul>
        {filteredTickets.map(ticket => (
          <li key={ticket.departure_time}>
            <p>{ticket.origin_name} → {ticket.destination_name}</p>
            <p>Дата: {ticket.departure_date}, Время вылета: {ticket.departure_time}, Время прибытия: {ticket.arrival_time}</p>
            <p>Пересадки: {ticket.stops}</p>
            <p>Цена: {ticket.price} руб.</p>
            <p>Авиакомпания: {ticket.carrier}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TicketList;
