export const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDate(); 
    const month = date.toLocaleString('ru-RU', { month: 'short' });
    const year = date.getFullYear();
    const weekday = date.toLocaleString('ru-RU', { weekday: 'short' });
    const capitalizedWeekday = weekday.charAt(0).toUpperCase() + weekday.slice(1);
    return `${day} ${month} ${year}, ${capitalizedWeekday}`;
  };
  
  export const getStopsText = (stops) => {
    if (stops === 0) return 'Без пересадок';
    if (stops === 1) return '1 пересадка';
    if (stops > 1 && stops < 5) return `${stops} пересадки`;
    return `${stops} пересадок`;
  };
