import React from 'react';

const StopsFilter = ({ selectedStops, onStopsChange }) => (
  <div>
    <h3>Количество пересадок</h3>
    <div className="stops-filter">
      <label>
        <input
          type="checkbox"
          value="all"
          checked={selectedStops.includes("all")}
          onChange={onStopsChange}
        />
        Все
      </label>
      {[0, 1, 2, 3].map((stops) => (
        <label key={stops}>
          <input
            type="checkbox"
            value={stops}
            checked={selectedStops.includes(stops)}
            onChange={onStopsChange}
          />
          {stops === 0 ? 'Без пересадок' : `${stops} пересадки`}
        </label>
      ))}
    </div>
  </div>
);

export default StopsFilter;
