import React, { useState } from 'react';
import ticketsData from './tickets.json'; // Импортируем данные о билетах
import CurrencyConverter from './CurrencyConverter'; // Импортируем компонент для конвертации валют

const TicketList = () => {
  const [filterStops, setFilterStops] = useState('all'); // Добавляем состояние для фильтра по пересадкам
  const [selectedCurrency, setSelectedCurrency] = useState('USD'); // Состояние для валюты

  // Сортировка билетов по цене
  const sortedTickets = [...ticketsData.tickets].sort((a, b) => a.price - b.price);

  // Фильтрация билетов по количеству пересадок
  const filteredTickets = sortedTickets.filter(ticket => {
    if (filterStops === 'all') return true; // Если выбрано "Все", не фильтруем
    return ticket.stops === parseInt(filterStops); // Фильтруем по количеству пересадок (точное совпадение)
  });

  return (
    <div>
      <h1>Билеты</h1>

      {/* Фильтрация по пересадкам */}
      <div>
        <label htmlFor="stops">Фильтр по пересадкам:</label>
        <select id="stops" value={filterStops} onChange={e => setFilterStops(e.target.value)}>
          <option value="all">Все</option>
          <option value="0">Без пересадок</option>
          <option value="1">1 пересадка</option>
          <option value="2">2 пересадки</option>
          <option value="3">3 пересадки</option>
        </select>
      </div>

      {/* Выбор валюты */}
      <div>
        <label htmlFor="currency">Выберите валюту:</label>
        <select 
          id="currency" 
          value={selectedCurrency} 
          onChange={e => setSelectedCurrency(e.target.value)} // Обновляем валюту
        >
          <option value="USD">USD</option>
          <option value="EUR">EUR</option>
          <option value="RUB">RUB</option>
        </select>
      </div>

      {/* Список билетов */}
      <ul>
        {filteredTickets.length === 0 ? (
          <li>Нет билетов для отображения</li>
        ) : (
          filteredTickets.map(ticket => (
            <li key={ticket.departure_time}>
              <p>{ticket.origin_name} → {ticket.destination_name}</p>
              <p>Дата: {ticket.departure_date}, Время вылета: {ticket.departure_time}, Время прибытия: {ticket.arrival_time}</p>
              <p>Пересадки: {ticket.stops}</p>
              <CurrencyConverter priceInRub={ticket.price} selectedCurrency={selectedCurrency} />
              <p>Авиакомпания: {ticket.carrier}</p>
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default TicketList;
