import React, { useState } from 'react';
import ticketsData from './tickets.json';
import CurrencyConverter from './CurrencyConverter';
import './TicketList.css';

const TicketList = () => {
  const [selectedStops, setSelectedStops] = useState('all'); // Значение 'all' по умолчанию
  const [selectedCurrency, setSelectedCurrency] = useState('RUB');

  // Фильтрация билетов
  const filteredTickets = ticketsData.tickets.filter((ticket) => {
    const stops = Number(ticket.stops);
    if (selectedStops === 'all') return true; // Все
    return selectedStops === stops; // Фильтрация по количеству пересадок
  });

  // Обработка фильтра пересадок
  const handleStopsChange = (e) => {
    setSelectedStops(e.target.value);
  };

  // Обработка переключения валют
  const handleCurrencyChange = (currency) => setSelectedCurrency(currency);

  return (
    <div className="ticket-list">
      <div className="sidebar">
        <h3>Валюта</h3>
        <div className="currency-buttons">
          {["RUB", "USD", "EUR"].map((currency) => (
            <button
              key={currency}
              className={selectedCurrency === currency ? "active" : ""}
              onClick={() => handleCurrencyChange(currency)}
            >
              {currency}
            </button>
          ))}
        </div>

        <h3>Количество пересадок</h3>
        <div className="stops-filter">
          <label>
            <input
              type="radio"
              value="all"
              checked={selectedStops === 'all'}
              onChange={handleStopsChange}
            />
            Все
          </label>
          <label>
            <input
              type="radio"
              value="0"
              checked={selectedStops === '0'}
              onChange={handleStopsChange}
            />
            Без пересадок
          </label>
          <label>
            <input
              type="radio"
              value="1"
              checked={selectedStops === '1'}
              onChange={handleStopsChange}
            />
            1 пересадка
          </label>
          <label>
            <input
              type="radio"
              value="2"
              checked={selectedStops === '2'}
              onChange={handleStopsChange}
            />
            2 пересадки
          </label>
          <label>
            <input
              type="radio"
              value="3"
              checked={selectedStops === '3'}
              onChange={handleStopsChange}
            />
            3 пересадки
          </label>
        </div>
      </div>

      <div className="tickets">
        {filteredTickets.length === 0 ? (
          <p>Нет билетов для отображения по выбранному фильтру.</p>
        ) : (
          filteredTickets.map((ticket) => (
            <div
              className="ticket"
              key={`${ticket.departure_time}-${ticket.arrival_time}-${ticket.stops}`}
            >
              <div className="ticket-left">
                <img src="airline-logo.png" alt="airline logo" />
                <button>
                  Купить за{" "}
                  <CurrencyConverter priceInRub={ticket.price} selectedCurrency={selectedCurrency} />
                </button>
              </div>

              <div className="ticket-right">
                <div className="ticket-time">
                  <p>{ticket.departure_time}</p>
                  <p>{ticket.stops} пересадок</p>
                  <p>{ticket.arrival_time}</p>
                </div>
                <div className="ticket-info">
                  <p>{ticket.origin}, {ticket.origin_name}</p>
                  <p>{ticket.destination_name}, {ticket.destination}</p>
                </div>
                <div className="ticket-info-date">
                  <p>{ticket.departure_date}</p>
                  <p>{ticket.arrival_date}</p>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default TicketList;
